  Alert,
  TextField,
} from '@mui/material';
import {
  ArrowBack as ArrowBackIcon,
  PlayArrow as PlayIcon,
  Home as HomeIcon,
  Check as CheckIcon,
  Refresh as RefreshIcon,
  Timer as TimerIcon,
  Speed as SpeedIcon,
  Accuracy as AccuracyIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { tomorrow } from 'react-syntax-highlighter/dist/esm/styles/prism';
import { Exercise, Language } from '../types/api';
import { exerciseApi, languageApi, sessionApi } from '../services/api';

const ExerciseCard: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [exercise, setExercise] = useState<Exercise | null>(null);
  const [language, setLanguage] = useState<Language | null>(null);
  const [loading, setLoading] = useState(true);
  const [userCode, setUserCode] = useState('');
  const [isStarted, setIsStarted] = useState(false);
  const [startTime, setStartTime] = useState<number | null>(null);
  const [timeSpent, setTimeSpent] = useState(0);
  const [isCorrect, setIsCorrect] = useState<boolean | null>(null);
  const [wpm, setWpm] = useState(0);
  const [accuracy, setAccuracy] = useState(0);

  const exerciseId = id;

  useEffect(() => {
    if (exerciseId) {
      loadExercise();
    }
  }, [exerciseId]);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isStarted && startTime) {
      interval = setInterval(() => {
        setTimeSpent(Date.now() - startTime);
      }, 100);
    }
    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [isStarted, startTime]);

  const getSyntaxLanguage = (languageName: string) => {
    switch (languageName.toLowerCase()) {
      case 'javascript': return 'javascript';
      case 'typescript': return 'typescript';
      case 'python': return 'python';
      case 'go': return 'go';
      case 'rust': return 'rust';
      default: return 'javascript';
    }
  };

  const calculateWPM = (code: string, timeMs: number) => {
    const words = code.trim().split(/\s+/).length;
    const minutes = timeMs / 60000;
    return minutes > 0 ? Math.round(words / minutes) : 0;
  };

  const calculateAccuracy = (userCode: string, targetCode: string) => {
    if (targetCode.length === 0) return 100;
    
    const maxLength = Math.max(userCode.length, targetCode.length);
    let correctChars = 0;
    
    for (let i = 0; i < Math.min(userCode.length, targetCode.length); i++) {
      if (userCode[i] === targetCode[i]) {
        correctChars++;
      }
    }
    
    return Math.round((correctChars / maxLength) * 100);
  };

  const loadExercise = async () => {
    if (!exerciseId) return;
    
    try {
      const exerciseData = await exerciseApi.getExercise(exerciseId);
      setExercise(exerciseData);
      
      // Загружаем информацию о языке
      await loadLanguage(exerciseData.language_id);
    } catch (error) {
      console.error('Error loading exercise:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadLanguage = async (languageId: number) => {
    try {
      const languageData = await languageApi.getLanguage(languageId.toString());
      setLanguage(languageData);
    } catch (error) {
      console.error('Error loading language:', error);
    }
  };

  const handleStart = () => {
    setIsStarted(true);
    setStartTime(Date.now());
    setTimeSpent(0);
  };

  const handleCheck = async () => {
    if (!exercise || !exerciseId) return;

    // Простая проверка - сравниваем код пользователя с эталонным
    const normalizedUserCode = userCode.trim().replace(/\s+/g, ' ');
    const normalizedExerciseCode = exercise.code.trim().replace(/\s+/g, ' ');
    
    const correct = normalizedUserCode === normalizedExerciseCode;
    const finalAccuracy = calculateAccuracy(userCode, exercise.code);
    const finalWpm = calculateWPM(userCode, timeSpent);
    
    setIsCorrect(correct);
    setAccuracy(finalAccuracy);
    setWpm(finalWpm);

    // Сохраняем сессию в базу данных
    try {
      await sessionApi.createSession({
        user_id: 1, // Временно используем ID 1, так как у нас нет аутентификации
        exercise_id: parseInt(exerciseId),
        user_code: userCode,
        is_correct: correct,
        time_spent: timeSpent,
        wpm: finalWpm,
        accuracy: finalAccuracy,
      });
    } catch (error) {
      console.error('Error saving session:', error);
    }
  };

  const handleReset = () => {
    setIsStarted(false);
    setStartTime(null);
    setTimeSpent(0);
    setUserCode('');
    setIsCorrect(null);
    setWpm(0);
    setAccuracy(0);
  };

  const handleBack = () => {
    // Возвращаемся к списку упражнений с сохранением фильтров
    const returnUrl = new URLSearchParams(window.location.search).get('return');
    if (returnUrl) {
      navigate(`/select-language${returnUrl}`);
    } else {
      navigate('/select-language');
    }
  };

  const handleHomeClick = () => {
    navigate('/');
  };

  const formattedTime = (milliseconds: number) => {
    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" sx={{ background: '#1da1f2' }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Загрузка...
            </Typography>
          </Toolbar>
        </AppBar>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh' }}>
          <Typography>Загрузка упражнения...</Typography>
        </Box>
      </Box>
    );
  }

  if (!exercise) {
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" sx={{ background: '#1da1f2' }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Упражнение не найдено
            </Typography>
          </Toolbar>
        </AppBar>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh' }}>
          <Typography>Упражнение не найдено</Typography>
        </Box>
      </Box>
    );
  }

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
      {/* Header */}
      <AppBar position="static" sx={{ background: '#1da1f2' }}>
        <Toolbar>
          <Button
            startIcon={<ArrowBackIcon />}
            onClick={handleBack}
            sx={{ color: 'white', mr: 2 }}
          >
            Назад
          </Button>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {exercise.title}
          </Typography>
          <Button
            startIcon={<PlayIcon />}
            onClick={handleHomeClick}
            sx={{ color: 'white' }}
          >
            Главная
          </Button>
        </Toolbar>
      </AppBar>

      <Box sx={{ p: 3 }}>
        <Grid container spacing={3}>
          {/* Левая панель - описание и код */}
          <Grid item xs={12} md={6}>
            <Stack spacing={3}>
              {/* Описание упражнения */}
              <Paper sx={{ p: 3 }}>
                <Typography variant="h5" gutterBottom sx={{ fontWeight: 600 }}>
                  {exercise.title}
                </Typography>
                <Typography variant="body1" color="text.secondary" paragraph>
                  {exercise.description}
                </Typography>
                <Stack direction="row" spacing={1}>
                  <Chip 
                    label={language?.name || 'Unknown'} 
                    color="primary" 
                    variant="outlined"
                  />
                  <Chip 
                    label={
                      exercise.difficulty === 'easy' ? 'Легко' :
                      exercise.difficulty === 'medium' ? 'Средне' : 'Сложно'
                    }
                    color={
                      exercise.difficulty === 'easy' ? 'success' :
                      exercise.difficulty === 'medium' ? 'warning' : 'error'
                    }
                  />
                </Stack>
              </Paper>

              {/* Эталонный код */}
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  Эталонный код:
                </Typography>
                <Box sx={{ 
                  position: 'relative',
                  border: '1px solid #e0e0e0',
                  borderRadius: 1,
                  overflow: 'hidden'
                }}>
                  <SyntaxHighlighter
                    language={getSyntaxLanguage(language?.name || 'javascript')}
                    style={tomorrow}
                    customStyle={{
                      margin: 0,
                      fontSize: '14px',
                      lineHeight: '1.5',
                    }}
                  >
                    {exercise.code}
                  </SyntaxHighlighter>
                </Box>
              </Paper>
            </Stack>
          </Grid>

          {/* Правая панель - ввод кода и результаты */}
          <Grid item xs={12} md={6}>
            <Stack spacing={3}>
              {/* Статистика */}
              {isStarted && (
                <Paper sx={{ p: 2 }}>
                  <Grid container spacing={2}>
                    <Grid item xs={3}>
                      <Typography variant="body2" color="text.secondary">
                        Время
                      </Typography>
                      <Typography variant="h6">
                        {formattedTime(timeSpent)}
                      </Typography>
                    </Grid>
                    <Grid item xs={3}>
                      <Typography variant="body2" color="text.secondary">
                        WPM
                      </Typography>
                      <Typography variant="h6">
                        {wpm}
                      </Typography>
                    </Grid>
                    <Grid item xs={3}>
                      <Typography variant="body2" color="text.secondary">
                        Точность
                      </Typography>
                      <Typography variant="h6">
                        {accuracy}%
                      </Typography>
                    </Grid>
                    <Grid item xs={3}>
                      <Typography variant="body2" color="text.secondary">
                        Статус
                      </Typography>
                      <Typography variant="h6">
                        {isCorrect === null ? 'В процессе' : 
                         isCorrect ? '✅ Правильно' : '❌ Неправильно'}
                      </Typography>
                    </Grid>
                  </Grid>
                </Paper>
              )}

              {/* Результат */}
              {isCorrect !== null && (
                <Alert 
                  severity={isCorrect ? 'success' : 'error'}
                  sx={{ mb: 2 }}
                >
                  {isCorrect 
                    ? 'Отлично! Код написан правильно.' 
                    : 'Попробуйте еще раз. Проверьте синтаксис и логику.'
                  }
                </Alert>
              )}

              {/* Кнопки управления */}
              <Stack direction="row" spacing={2}>
                {!isStarted ? (
                  <Button
                    variant="contained"
                    startIcon={<PlayIcon />}
                    onClick={handleStart}
                    fullWidth
                  >
                    Начать упражнение
                  </Button>
                ) : (
                  <>
                    <Button
                      variant="contained"
                      startIcon={<CheckIcon />}
                      onClick={handleCheck}
                      disabled={!userCode.trim()}
                      sx={{ flex: 1 }}
                    >
                      Проверить
                    </Button>
                    <Button
                      variant="outlined"
                      startIcon={<RefreshIcon />}
                      onClick={handleReset}
                      sx={{ flex: 1 }}
                    >
                      Сбросить
                    </Button>
                  </>
                )}
              </Stack>

              {/* Редактор кода */}
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  Ваш код:
                </Typography>
                <TextField
                  fullWidth
                  multiline
                  rows={12}
                  variant="outlined"
                  placeholder="Начните писать код здесь..."
                  value={userCode}
                  onChange={(e) => setUserCode(e.target.value)}
                  disabled={!isStarted}
                  sx={{
                    '& .MuiInputBase-root': {
                      fontFamily: 'monospace',
                      fontSize: '14px',
                      lineHeight: '1.5',
                    }
                  }}
                />
              </Paper>
            </Stack>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default ExerciseCard;
