import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Card,
  Button,
  LinearProgress,
  Alert,
  Paper,
} from '@mui/material';
import { PlayArrow as PlayIcon, Check as CheckIcon, Refresh as RefreshIcon, BarChart as BarChartIcon, ThumbUp as ThumbUpIcon, Visibility as VisibilityIcon } from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import MonacoEditor from '@monaco-editor/react';
import { Exercise, Language } from '../types/api';
import { exerciseApi, languageApi, sessionApi } from '../services/api';

const ExerciseCard: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [exercise, setExercise] = useState<Exercise | null>(null);
  const [language, setLanguage] = useState<Language | null>(null);
  const [loading, setLoading] = useState(true);
  const [userCode, setUserCode] = useState('');
  const [isStarted, setIsStarted] = useState(false);
  const [isCorrect, setIsCorrect] = useState<boolean | null>(null);
  const [resultMsg, setResultMsg] = useState('');
  const [showStats, setShowStats] = useState(false);

  useEffect(() => {
    if (id) {
      loadExercise();
    }
  }, [id]);

  const loadExercise = async () => {
    try {
      const exerciseData = await exerciseApi.getExercise(id!);
      setExercise(exerciseData);
      const languageData = await languageApi.getLanguage(exerciseData.language_id.toString());
      setLanguage(languageData);
    } catch (error) {
      console.error('Error loading exercise:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStart = () => {
    setIsStarted(true);
    setUserCode(exercise?.code || '');
    setIsCorrect(null);
    setResultMsg('');
  };

  const handleCheck = () => {
    if (!exercise) return;
    const normalizedUserCode = userCode.trim().replace(/\s+/g, ' ');
    const normalizedExerciseCode = exercise.code.trim().replace(/\s+/g, ' ');
    const correct = normalizedUserCode === normalizedExerciseCode;
    setIsCorrect(correct);
    setResultMsg(correct ? 'Поздравляем! Ваш код правильный!' : 'Код не совпадает, попробуйте ещё раз.');
  };

  const handleReset = () => {
    setIsStarted(false);
    setUserCode('');
    setIsCorrect(null);
    setResultMsg('');
  };

  const handleShowStats = () => setShowStats((prev) => !prev);

  const attempts = exercise?.attempts || 0;
  const successful = exercise?.successful_attempts || 0;

  if (loading || !exercise) {
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: 'background.default' }}>
        <LinearProgress sx={{ width: '100%' }} />
      </Box>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Card sx={{ mb: 3, p: 2, borderRadius: 3, boxShadow: 1, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box>
          <Typography variant="h6" sx={{ mb: 1, fontWeight: 'bold' }}>Описание</Typography>
          <Typography variant="body1">{exercise.description}</Typography>
        </Box>
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', minWidth: 160 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 0.5 }}>
            <BarChartIcon color="primary" sx={{ mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 500 }}>Попыток:&nbsp;</Typography>
            <Typography variant="body2" color="text.secondary">{attempts}</Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <ThumbUpIcon color="success" sx={{ mr: 0.5 }} />
            <Typography variant="body2" sx={{ fontWeight: 500 }}>Успешных:&nbsp;</Typography>
            <Typography variant="body2" color="text.secondary">{successful}</Typography>
          </Box>
        </Box>
      </Card>
      <Card sx={{ mb: 3, p: 2, borderRadius: 3, boxShadow: 1 }}>
        {!isStarted ? (
          <>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <VisibilityIcon color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6" sx={{ fontWeight: 'bold', fontSize: '1.15rem' }}>Код для повторения</Typography>
            </Box>
            <Box sx={{ position: 'relative', mb: 2 }}>
              <Paper variant="outlined" sx={{ borderRadius: 2, overflow: 'hidden', background: '#fffde7', boxShadow: 0 }}>
                <MonacoEditor
                  height="180px"
                  defaultLanguage={language?.name.toLowerCase() || 'javascript'}
                  value={exercise.code}
                  options={{ readOnly: true, fontSize: 16, minimap: { enabled: false }, scrollBeyondLastLine: false }}
                />
              </Paper>
            </Box>
            <Button
              variant="contained"
              startIcon={<PlayIcon />}
              size="large"
              sx={{ fontWeight: 'bold', px: 4, py: 1.5, borderRadius: 3 }}
              onClick={() => {
                setIsStarted(true);
                setUserCode('');
                setIsCorrect(null);
                setResultMsg('');
              }}
            >
              Начать выполнение
            </Button>
          </>
        ) : (
          <>
            <Typography variant="h6" sx={{ mb: 1, fontWeight: 'bold' }}>Введите код</Typography>
            <Paper variant="outlined" sx={{ mb: 2, borderRadius: 2, overflow: 'hidden' }}>
              <MonacoEditor
                height="180px"
                defaultLanguage={language?.name.toLowerCase() || 'javascript'}
                value={userCode}
                onChange={(v: string | undefined) => setUserCode(v || '')}
                options={{ fontSize: 16, minimap: { enabled: false }, scrollBeyondLastLine: false, readOnly: false }}
              />
            </Paper>
            <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
              <Button
                variant="contained"
                color="primary"
                startIcon={<CheckIcon />}
                sx={{ fontWeight: 'bold', borderRadius: 3 }}
                onClick={handleCheck}
              >
                Проверить
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                startIcon={<RefreshIcon />}
                sx={{ fontWeight: 'bold', borderRadius: 3 }}
                onClick={handleReset}
              >
                Сбросить
              </Button>
            </Box>
            {isCorrect !== null && (
              <Alert severity={isCorrect ? 'success' : 'error'} sx={{ mt: 2, fontWeight: 'bold' }}>
                {resultMsg}
              </Alert>
            )}
          </>
        )}
      </Card>
    </Container>
  );
};

export default ExerciseCard;
